/**
 * The View Engine compiler generates reexports which have been prefixed with this identifier.
 * See packages/compiler-cli/src/metadata/bundler.ts for the corresponding prefix definition in
 * the View Engine compiler.
 */
const VE_PRIVATE_REEXPORT_PREFIX = '\u0275ngve$';

/**
 * Exports that have been generated by the View Engine compiler are not considered by the Ivy
 * compiler as their names may change between versions, making them unsuitable for Ivy's partial
 * compilation mode. In partial compilation, the generated partial declarations have to refer to a
 * library declarations using their stable export name.
 */
export function shouldExcludeExport(exportName: string): boolean {
  return exportName.startsWith(VE_PRIVATE_REEXPORT_PREFIX);
}
